mapper.select:
SELECT B.board_no, B.board_title, B.board_content, m.member_id, DATE_FORMAT(B.reg_date, '%Y-%m-%d %H:%i:%s') AS reg_date, DATE_FORMAT(B.mod_date, '%Y-%m-%d %H:%i:%s') AS mod_date FROM board B JOIN member m ON B.board_writer = m.member_no
(B.board_title LIKE CONCAT('%', #{keyword}, '%') OR M.member_id LIKE CONCAT('%', #{keyword}, '%'))
LIMIT #{limitPageNo},#{numPerPage}
